property.code:
require.config({
    paths : {
        jstree : "$doc.getAttachmentURL('jstree.min')",
    },
    shim : {
        jstree : {
            'export' : "jstree"
        }
    }
});

require(['jQueryNoConflict', 'jstree'], function ($, jstree) {

    var xwikiExplorer = xwikiExplorer || {};
    var enc = encodeURIComponent;

    xwikiExplorer = function () {
        var that = this;
        this.childrenIcon = "$xwiki.getSkinFile('icons/silk/page_white.png')";
        this.baseURL = XWiki.contextPath;
        this.wiki = XWiki.currentWiki;
        var reqJson = "?media=json";

        this.init = function (element) {
            var spacesURL = this.baseURL + '/rest/wikis/' + this.wiki + '/spaces' + reqJson;
            console.log("Initializing tree...");
            $(element).jstree({
                'core' : {
                    'themes' : {
                        'responsive' : false,
                        'dots' : false
                    },
                    'multiple' : false,
                    'data' : {
                        'url' :
                        function (node) {
                            return node.id === '#' ?
                            spacesURL : getNodeUrl(node);
                        },
                        dataFilter : function (data, type) {
                            //rename json keys to be recognized by jsTree
                            var parsedJson = JSON.parse(data, function (k, v) {
                                    if (k === "name")
                                        this.text = v;
                                    else if (k === "pageName")
                                        this.text = v;
                                    else
                                        return v;
                                });
                            //check if the request goes for spaces
                            if (parsedJson.spaces != undefined) {
                                parsedJson = parsedJson.spaces;
                                $.each(parsedJson, function (i, item) {
                                    parsedJson[i].children = true;
                                    parsedJson[i].type = "root";
                                    parsedJson[i].a_attr = {
                                        "href" : that.baseURL + "/bin/view/Main/SpaceIndex?space=" + enc(parsedJson[i].text)
                                    }
                                })
                                //or xwiki pages
                            } else {
                                parsedJson = parsedJson.searchResults;
                                //check if the xwiki page has children
                                $.each(parsedJson, function (i, item) {
                                    parsedJson[i].a_attr = {
                                        "href" : that.baseURL + "/bin/view/" + enc(parsedJson[i].space) + "/" + enc(parsedJson[i].text)
                                    }
                                    if (hasChildren(parsedJson[i].space, parsedJson[i].text) == true) {
                                        parsedJson[i].children = true;
                                    }
                                    if (typeof that.childrenIcon !== "undefined") {
                                        parsedJson[i].icon = that.childrenIcon;
                                    }
                                })
                            }
                            return JSON.stringify(parsedJson);
                        }
                    }
                },
                'plugins' : ["contextmenu", "dnd", "search", "types", "wholerow"],
                'contextmenu' : {
                    'items' : customMenu
                }
            })
        }

        this.destroy = function (element) {
            $(element).jstree(true).destroy();
        }

        function customMenu(node) {
            //Show a different label for renaming files and folders
            var items = {
                "open" : {
                    "label" : "Open",
                    "action" : function (obj) {
                        window.open(node.a_attr.href, '_blank', 'location=yes,scrollbars=yes,status=yes');
                    },
                    "separator_after" : 'true'
                },
                "upload" : {
                    "label" : "New",
                    "action" : function (obj) {
                        var alphaExp = /^[a-zA-Z0-9]+/;
                        var pageName = prompt('Name of the new page');
                        var dupPage = false;
                        $.ajax({
                            url : that.baseURL + '/rest/wikis/' + that.wiki + '/spaces/' + enc(node.original.space) + '/pages/' + enc(pageName),
                            type : 'GET',
                            async : false,
                            success : function (data) {
                                if (data) {
                                    dupPage = true;
                                }
                            }
                        });
                        if (pageName == null) {
                            //null is returned if dialog is canceled
                        } else if (pageName == "") {
                            alert("Page name is required");
                        } else if (!pageName.match(alphaExp)) {
                            alert("Page name is not allowed")
                        } else if (dupPage) {
                            alert("The page already exists. Please fill in a new page name. ")
                        } else {
                            var parent = node.original.space + "." + node.text;
                            var space = node.original.space;
                            if (node.parent === '#') {
                                parent = "";
                                space = node.original.text;
                            }
                            $.ajax({
                                url : that.baseURL + '/rest/wikis/' + that.wiki + '/spaces/' + enc(space) + '/pages/' + enc(pageName),
                                type : 'PUT',
                                data : { 'title' : pageName, 'parent' : parent },
                                async : false,
                                success : function (data) {
                                    alert("The page was created successfully");
                                    $('#tree').jstree('refresh', $(document.getElementById('node_' + node.id)));
                                },
                                error : function (XMLHttpRequest, textStatus, errorThrown) {
                                    alert("Status: " + textStatus + " / Error: " + errorThrown);
                                }
                            });
                        }
                    },
                },
                /*"rename" : {
                "label" : "Rename",
                "action": function(obj){
                TODO
                }
                },*/
                "remove" : {
                    "label" : "Delete",
                    "action" : function (obj) {
                        if (confirm('Are you sure you wish to move this document to the recycle bin?')) {
                            $.ajax({
                                url : that.baseURL + '/rest/wikis/' + that.wiki + '/spaces/' + enc(node.original.space) + '/pages/' + enc(node.text),
                                type : 'DELETE',
                                async : false,
                                success : function (data) {
                                    alert("The document has been deleted.");
                                    $('#tree').jstree('refresh', $(document.getElementById('node_' + node.id)));
                                },
                                error : function (XMLHttpRequest, textStatus, errorThrown) {
                                    alert("Status: " + textStatus + " / Error: " + errorThrown);
                                }
                            });
                        } else {
                            //do nothing
                        }
                    }
                }
            };
            if (node.parent === '#') {
                delete items.remove;
                delete items.rename;
            }
            return items;
        }

        function hasChildren(space, page) {
            var hasChildren = false;
            $.ajax({
                url : that.baseURL + '/rest/wikis/' + that.wiki + '/spaces/' + enc(space) + '/pages/' + enc(page) + '/children' + reqJson,
                dataType : 'json',
                async : false,
                success : function (data) {
                    if (data.pageSummaries.length != 0) {
                        hasChildren = true;
                    }
                }
            });
            return hasChildren;
        }

        function getNodeUrl(node) {
            //template URL> http://localhost:8148/industrialwiki/rest/wikis/xwiki/query?q={query}(&type={xwql,hql,lucene})(&number={number})(&start={start})(&orderField={fieldname}(&order={asc|desc}))(&distinct=1)(&prettyNames={false|true})(&wikis={wikis})(&className={classname})
            var slice = node.id.indexOf(":") + 1;
            //check weather parent or node is space or page
            if (node.id.indexOf(".") != -1) {
                //get fullPageName from node.id
                var fullPageName = node.id.slice(slice, node.id.length);
                var hql = "where doc.parent = '" + enc(fullPageName.replace(/'/g, "''")) + "' and doc.name != 'WebHome' and doc.language ='' order by doc.name";
                var url = that.baseURL + "/rest/wikis/" + that.wiki + "/query?q=" + enc(hql) + "&type=hql&media=json";
            } else {
                //get space from node.id
                var space = node.id.slice(slice, node.id.length);
                //Query gets documents with parent pages which no longer exist, documents with no parent page and <space>.WebHome, documents with parents in other spaces are excluded
                var hql = "where doc.space = '" + space + "' and (doc.parent not in (SELECT DISTINCT doc.parent.fullName FROM XWikiDocument doc where doc.space = '" + space + "') or doc.name ='WebHome' or doc.parent ='')and doc.name not in (SELECT DISTINCT doc.name FROM XWikiDocument doc where doc.parent not like '" + space + "%25' and doc.space = '" + space + "' and doc.parent !='' and doc.name !='WebHome') and doc.language ='' order by doc.name";
                var url = that.baseURL + "/rest/wikis/" + that.wiki + "/query?q=" + enc(hql) + "&type=hql&media=json";
            }
            return url;
        }

        this.setChildrenIcon = function (m_ChildrenIcon) {
            if (typeof m_ChildrenIcon !== "undefined") {
                this.childrenIcon = m_ChildrenIcon;
            }
        }

        this.setWiki = function (m_Wiki) {
            if (typeof m_Wiki !== "undefined") {
                this.wiki = m_Wiki;
            }
        }

        this.setBaseURL = function (m_BaseURL) {
            if (typeof m_BaseURL !== "undefined") {
                this.baseURL = m_BaseURL;
            }
        }
    };
    var xwikiExplorer = new xwikiExplorer();
    xwikiExplorer.init("#tree");
});

